import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import '../../styles/Sidebar.css';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();
  const currentPath = location.pathname;

  // This should come from your authentication context/state
  const userRole = 'student'; // or 'teacher' or 'admin'

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const user = {
    name: userRole === 'student' ? 'Student User' : userRole === 'teacher' ? 'Teacher User' : 'Admin User',
    role: userRole,
    avatar: null
  };

  // Different menu items based on user role
  const getMenuItems = () => {
    if (userRole === 'student') {
      return [
        { path: '/dashboard', label: 'Dashboard', icon: 'bi-grid' },
        { path: '/dashboard/assignments', label: 'Assignments', icon: 'bi-journal-text' },
        { path: '/dashboard/settings', label: 'Settings', icon: 'bi-gear' }
      ];
    }
    return [
      { path: '/dashboard', label: 'Dashboard', icon: 'bi-grid' },
      { path: '/dashboard/students', label: 'Students', icon: 'bi-people' },
      { path: '/dashboard/courses', label: 'Classes', icon: 'bi-book' },
      { path: '/dashboard/assignments', label: 'Assignments', icon: 'bi-journal-text' },
      { path: '/dashboard/settings', label: 'Settings', icon: 'bi-gear' }
    ];
  };

  const menuItems = getMenuItems();

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3 className="sidebar-brand">
          <span className="brand-text">SVS</span>
          {isOpen && <span className="ms-2">Portal</span>}
        </h3>
        <button className="sidebar-toggle d-md-none" onClick={toggleSidebar}>
          <i className={`bi ${isOpen ? 'bi-x' : 'bi-list'}`}></i>
        </button>
      </div>
      
      <div className="sidebar-user" onClick={() => navigate('/dashboard/settings')}>
        <div className="user-avatar" title={user.name}>
          {user.avatar ? (
            <img 
              src={user.avatar} 
              alt={user.name} 
              className="rounded-circle w-100 h-100"
            />
          ) : (
            <i className="bi bi-person-circle"></i>
          )}
        </div>
        {isOpen && (
          <div className="user-info">
            <div className="user-name" title={user.name}>{user.name || 'User'}</div>
            <div className="user-role" title={user.role}>
              {user.role ? user.role.charAt(0).toUpperCase() + user.role.slice(1) : 'Guest'}
            </div>
          </div>
        )}
      </div>
      
      <ul className="sidebar-menu">
        {menuItems.map((item, index) => (
          <li key={index} className={`sidebar-menu-item ${currentPath === item.path ? 'active' : ''}`}>
            <Link to={item.path} className="sidebar-menu-link">
              <i className={`bi ${item.icon}`}></i>
              {isOpen && <span>{item.label}</span>}
            </Link>
          </li>
        ))}
      </ul>
      
      <div className="sidebar-footer">
        {isOpen && (
          <div className="school-info">
            <p className="mb-0">Sunshine Valley School</p>
            <small>Learning Management System</small>
          </div>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
