"use client"

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import Image from 'next/image';

const UserSettings = () => {
  const router = useRouter();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    role: '',
    subject: '',
    department: '',
    password: '',
    confirmPassword: ''
  });
  
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [avatar, setAvatar] = useState('/placeholder.svg?height=100&width=100');
  
  useEffect(() => {
    // Check if running in browser
    if (typeof window !== 'undefined') {
      const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
      if (!isAuthenticated) {
        router.push('/signin');
        return;
      }
      
      // Load user data from localStorage
      const userData = JSON.parse(localStorage.getItem('user') || '{}');
      setFormData({
        name: userData.name || '',
        email: userData.email || '',
        role: userData.role || '',
        subject: userData.subject || '',
        department: userData.department || '',
        password: '',
        confirmPassword: ''
      });
    }
  }, [router]);
  
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validate form
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    
    // Get current user data
    const currentUserData = JSON.parse(localStorage.getItem('user') || '{}');
    
    // Update user data
    const updatedUserData = {
      ...currentUserData,
      name: formData.name,
      email: formData.email,
      subject: formData.subject,
      department: formData.department
    };
    
    // If password is provided, update it
    if (formData.password) {
      updatedUserData.password = formData.password;
    }
    
    // Save updated user data to localStorage
    localStorage.setItem('user', JSON.stringify(updatedUserData));
    
    // Dispatch custom event to notify other components
    if (typeof window !== 'undefined') {
      const event = new Event('userDataUpdated');
      window.dispatchEvent(event);
    }
    
    setSuccess('Settings updated successfully!');
    
    // Clear password fields
    setFormData({
      ...formData,
      password: '',
      confirmPassword: ''
    });
  };
  
  return (
    <div className="user-settings-container">
      <div className="settings-wrapper">
        <div className="page-header">
          <h1>User Settings</h1>
          <p>Update your profile information</p>
        </div>
        
        <div className="settings-card">
          <div className="settings-card-header">
            <h2>Profile Settings</h2>
          </div>
          
          <div className="settings-card-body">
            {success && <div className="alert alert-success mb-4 p-3 bg-green-100 text-green-700 rounded">{success}</div>}
            {error && <div className="alert alert-danger mb-4 p-3 bg-red-100 text-red-700 rounded">{error}</div>}
            
            <div className="avatar-section">
              <div className="avatar-preview">
                <Image 
                  src={avatar || "/placeholder.svg"} 
                  alt="User avatar" 
                  width={100} 
                  height={100}
                />
              </div>
              <div className="avatar-controls">
                <h3 className="mb-2 text-lg font-medium">Profile Picture</h3>
                <p className="mb-3 text-gray-600">Upload a new avatar or keep your current one</p>
                <button className="btn btn-outline-primary px-4 py-2 border border-gray-300 rounded">
                  Change Avatar
                </button>
              </div>
            </div>
            
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name" className="form-label">Name</label>
                <input
                  type="text"
                  className="form-control w-full border border-gray-300 rounded"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="email" className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control w-full border border-gray-300 rounded"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="subject" className="form-label">Subject</label>
                <input
                  type="text"
                  className="form-control w-full border border-gray-300 rounded"
                  id="subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleChange}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="department" className="form-label">Department</label>
                <input
                  type="text"
                  className="form-control w-full border border-gray-300 rounded"
                  id="department"
                  name="department"
                  value={formData.department}
                  onChange={handleChange}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="password" className="form-label">New Password (leave blank to keep current)</label>
                <input
                  type="password"
                  className="form-control w-full border border-gray-300 rounded"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="confirmPassword" className="form-label">Confirm New Password</label>
                <input
                  type="password"
                  className="form-control w-full border border-gray-300 rounded"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                />
              </div>
              
              <button type="submit" className="btn-save w-full bg-blue-600 text-white rounded">Save Changes</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserSettings;
